//         // type: ADD_FAV,
//         type: postFav,
//         payload: character
//     }
// }
// export const addFav = (character) => {
export const favoritos = (character) => {
    const endpoint = 'http://localhost:3001/rickandmorty/fav';
    return (dispatch) => {
       axios.post(endpoint, character).then(({ data }) => {
          return dispatch({
             type: 'ADD_FAV',
             payload: data,
          });
       });
    };
 };

// const remove = (id) => {
//     return {
//         //type: REMOVE_FAV,
//         type: deleteFav,
//         payload: id
//     }
// }
export const remove = (id) => {
    const endpoint = 'http://localhost:3001/rickandmorty/fav/' + id;
    return (dispatch) => {
       axios.delete(endpoint).then(({ data }) => {
          return dispatch({
             type: 'REMOVE_FAV',
             payload: data,
       });
       });
    };
 };

const filterCards = (status) => {
    return {
        type: FILTER,
        payload: status
    }
}

const orderCards = (id) => {
    return {
        type: ORDER,
        payload: id
    }
}

export {filterCards, orderCards}
